import React, { useState, useEffect } from "react";
import GetAllProducts from "../components/GetAllProducts";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useParams } from "react-router";
import Button from "react-bootstrap/Button";
import { useDispatch } from "react-redux";
import { addItem, delItem,allItem } from "../redux/actions/index";
import Form from "react-bootstrap/Form";

import AddItemsAxios from "./AddItemsAxios";
import DeleteItemsAxios from "./DeleteItemsAxios";


function AdminEditProductDetails() {
  const [data, setdata] = useState([]);
  const [cartBtn, setCartBtn] = useState("Add to Cart");
  const [qty,setqty]=useState(0);
  let d;
  

  // We need to store useDispatch in a variable
  const dispatch = useDispatch();

  useEffect(() => {
    GetAllProducts.getAllProducts().then((value) => {
      setdata(value);
      d = value;
      console.log("dygwewfiuhhfoiqhofioehfoiehfiwe ", value);
    });
  }, []);
  const proid = useParams();
  console.log("proid: ", proid.id);
  console.log("data: ", data);
  const proDetail = data.filter((x) => x.productId == proid.id);
  console.log("productDetail: ", proDetail);
  const product = proDetail[0];
  console.log(product);

  const func = () => {
    const handleCart = (product) => {

      let result;

      if (cartBtn === "Add to Cart") {
       AddItemsAxios.addItemsAxios(
          "http://localhost:8084/cart-service/cart/items/" +
            product.productName+"/"+qty
            
        ).then((value) => {
          result=value[0]
          console.log("result ", result);
        console.log("product ", product);
        dispatch(addItem(result));
        setCartBtn("Remove from Cart");
       

        
        });
        


        
      } else {
        DeleteItemsAxios.deleteItemsAxios(
            "http://localhost:8084/cart-service/cart/updateCart/deleteItem/" +
            product.productName 
        )



        dispatch(delItem(product));
        setCartBtn("Add to Cart");
      }
    };

    const funcSearch = (event) => {
      event.preventDefault();
      setqty(document.getElementById("Quantity").value) ;
      console.log(qty);
    };

    return (
      <div>
        <Container className="my-5 py-3">
          {
            <Row>
              <Col className="d-flex justify-content-center mx-auto product">
                <img
                  src={product.productImg}
                  alt={product.productName}
                  height="600px"
                />
                
              </Col>
              <Col className="d-flex flex-column justify-content-center">
              <Form>
            <Form.Group className="mb-3" controlId="formBasicproductName1">
              <Form.Label>Product Name</Form.Label>
              <Form.Control as="textarea" rows={5} placeholder="Enter Product Name" required />
            </Form.Group>
            
            <Form.Group className="mb-3" controlId="formBasicCategory1">
              <Form.Label>Category</Form.Label>
              <Form.Control type="text" placeholder="Enter Category" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPrice1">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" step="any" placeholder="Enter Price" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicDescription1">
              <Form.Label>Description</Form.Label>
              <Form.Control as="textarea" rows={10} placeholder="Enter Description" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicStockQuantity1">
              <Form.Label>Stock Quantity</Form.Label>
              <Form.Control type="number" placeholder="Enter Stock Quantity" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicProductImg1">
              <Form.Label>Product Image</Form.Label>
              <Form.Control type="text" placeholder="Enter Product Image URL" required />
            </Form.Group>
            
            <Button
              variant="outline-primary w-100 mt-5"
              type="submit"
              onClick={addProductsAxios}
            >
              Submit
            </Button>
          </Form>
              </Col>
            </Row>
          }
        </Container>
      </div>
    );
  };

  return React.Children.toArray(data.map(func)[0]);
}
export default AdminEditProductDetails;
