import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import ReactDOM from "react-dom";
import Card from "react-bootstrap/Card";
import GetAllProducts from "../components/GetAllProducts";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Dropdown from "react-bootstrap/Dropdown";
import DropdownButton from "react-bootstrap/DropdownButton";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";

function AdminEdit() {
  const [data, setdata] = useState([]);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  
  let d;
  useEffect(() => {
    GetAllProducts.getAllProducts().then((value) => {
      setdata(value);
      d = value;
      console.log("d ", d);
      console.log("token :",sessionStorage.getItem("token"))
    });
  }, []);

  const cardItem = (item) => {
    return (
      <Card className="my-5 py-4" style={{ width: "32rem" }}>
        <div>
          <Card.Img
            src={item.productImg}
            variant="top"
            style={{ width: "30rem", height: "32rem" }}
            className="justify-content-center"
          />
        </div>
        <Card.Body className="text-center my-5">
          <Card.Title>{item.productName}</Card.Title>
          <Card.Text>{item.category}</Card.Text>
          <Card.Text>₹{item.price}</Card.Text>
          {/* <Card.Text>{item.description}</Card.Text> */}

          <NavLink
            to={"/product/" + item.productId}
            className="btn btn-outline-primary ms-2"
          >
            Edit Product
          </NavLink>
        </Card.Body>
      </Card>
    );
  };

  const funcSearch = (event) => {
    event.preventDefault();
    let name = document.getElementById("Search").value;
    window.open("http://localhost:3000/product/Search/" + name, "_self");
  };


  const addProducts = (event) => {
    console.log("Add Products")
    return(
        <div>
       
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>ADD PRODUCT</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          
          <Form>
            <Form.Group className="mb-3" controlId="formBasicproductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control type="textarea" placeholder="Enter Product Name" required />
            </Form.Group>
            
            <Form.Group className="mb-3" controlId="formBasicCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control type="text" placeholder="Enter Category" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" step="any" placeholder="Enter Price" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicDescription">
              <Form.Label>Description</Form.Label>
              <Form.Control type="textarea" placeholder="Enter Description" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicStockQuantity">
              <Form.Label>Stock Quantity</Form.Label>
              <Form.Control type="number" placeholder="Enter Stock Quantity" required />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicProductImg">
              <Form.Label>Product Image</Form.Label>
              <Form.Control type="text" placeholder="Enter Product Image URL" required />
            </Form.Group>
            
            <Button
              variant="outline-primary w-100 mt-5"
              type="submit"
              //onClick={authenticate}
            >
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
      </div>
    )
  }

  const addProductsAxios = (event) => {
    console.log("Add Product Axios");
    let productName=document.getElementById("formBasicProductName").value;
    let category=document.getElementById("formBasicCategory").value;
    let price=document.getElementById("formBasicDescription").value;
    let description=document.getElementById("formBasicProductName").value;
    let StockQuantity=document.getElementById("formBasicStockQuantity").value;
    let ProductImage=document.getElementById("formBasicProductImage").value;
    bode={
        
            "productName" : productName,
            "category": category,
            "price": price,
            "description":"description",
            "stockQuantity": StockQuantity,
            "productImg": "ProductImage"
        
    }
  }

  return (
    <div>
      <Container className="py-5">
        <Row>
          <Col className="text-center">
            <h1>Admin Product Details</h1>
            <hr />
          </Col>
        </Row>
      </Container>
      <Container>
        <Row className="d-flex">
          <Col className="text-left d-flex">
            <DropdownButton
              variant="outline-primary"
              id="dropdown-basic-button"
              title="Select Category"
            >
              <Dropdown.Item href={"/product/Cat/" + "fashion"}>
                Fashion
              </Dropdown.Item>
              <Dropdown.Item href={"/product/Cat/" + "mobiles and tablets"}>
                Mobiles and Tablets
              </Dropdown.Item>
              <Dropdown.Item href={"/product/Cat/" + "consumer electronics"}>
                Consumer Electronics
              </Dropdown.Item>
              <Dropdown.Item href={"/product/Cat/" + "books"}>
                Books
              </Dropdown.Item>
              <Dropdown.Item href={"/product/Cat/" + "baby products"}>
                Baby Products
              </Dropdown.Item>
              <Dropdown.Item href={"/product/Cat/" + "home furnishings"}>
                Home Furnishings
              </Dropdown.Item>
            </DropdownButton>

            <Form onSubmit={funcSearch} className="d-flex ms-5">
              <Form.Group className="mb-3" controlId="Search">
                <Form.Control
                  type="text"
                  placeholder="Search"
                  style={{ width: "30rem" }}
                />
              </Form.Group>
              <Button
                className="ms-5"
                variant="outline-primary"
                type="submit"
                style={{ height: "2.5rem" }}
              >
                Search
              </Button>
              <Button
                className="ms-5"
                variant="outline-primary"
                onClick={handleShow}
                style={{ height: "2.5rem" }}
              >
                Add Products
              </Button>
              {show==true&&addProducts()}
            </Form>
          </Col>
        </Row>
      </Container>
      <Container id="products_all">
        <Row className="justify-content-around">
          {/* using React.childer to Arrya to set key automatically for the items of list */}
          {React.Children.toArray(data.map(cardItem))}
        </Row>
      </Container>
    </div>
  );
}

export default AdminEdit;
