const addItem = [];
const allItem=[];
export const addItems = (state = addItem, action) => {
  switch (action.type) {
    case "ADDITEM":

     
      return [...state, action.payload];
      break;

    case "DELITEM":
      return (state = state.filter((x) => {
        return x.productId !== action.payload.productId;
      }));
      break;

    default:
      return state;
      break;
  }
};
export const allItems = (state = allItem, action) => {
  switch (action.type) {
    case "ALLITEM":

     
      return [...state, action.payload];
      break;

    

    default:
      return state;
      break;
  }
};
