import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import axios from "axios";
/* import { DATA } from "../Data"; */

function Product() {
  const [data, setdata] = useState([]);
  let d;
  useEffect(() => {
    getAllProducts().then((value) => {
      setdata(value);
      d = value;
      console.log("d ", d);
    });
  }, []);

  const cardItem = (item) => {
    return (
      <Card className="my-5 py-4" style={{ width: "35rem" }}>
        <div style={{ width: "32rem", height: "24rem" }}>
          <Card.Img
            src={"/assets/images/product/product" + item.productId + ".jpg"}
            variant="top"
          />
        </div>
        <Card.Body className="text-center">
          <Card.Title>{item.productName}</Card.Title>
          <Card.Text>{item.category}</Card.Text>
          <Card.Text>{item.price}</Card.Text>
          <Card.Text>{item.description}</Card.Text>

          <NavLink
            to={"/product/" + item.productId}
            className="btn btn-outline-primary ms-2"
          >
            Buy Now
          </NavLink>
        </Card.Body>
      </Card>
    );
  };

  return (
    <div>
      <div className="container py-5">
        <div className="row">
          <div className="col-12 text-center">
            <h1>Product</h1>
            <hr />
          </div>
        </div>
      </div>
      <div className="container">
        {/* using React.childer to Arrya to set key automatically for the items of list */}
        <div className="row justify-content-around">
          {React.Children.toArray(data.map(cardItem))}
        </div>
      </div>
    </div>
  );
}

async function getAllProducts() {
  let url = "http://localhost:8084/cart-service/cart/available-items";
  let axiosResponse;
  let result;
  let error = "";
  console.log("in func");
  try {
    axiosResponse = await axios.get(url).catch((err) => {
      if (err.response.status !== 200) {
        console.log(err.response.status);
        error = err.response.status;
        throw new Error("CANNOT FETCH PRODUCT");
      }
      throw err;
    });
  } catch (err) {
    alert(err);
    result = err;
  }
  if (error === "") {
    result = axiosResponse.data;
  }
  console.log("resp ", axiosResponse);

  return result;
}

export default Product;
