import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

import Card from "react-bootstrap/Card";
import GetAllProducts from "../components/GetAllProducts";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

function Product() {
  const [data, setdata] = useState([]);
  let d;
  useEffect(() => {
    GetAllProducts.getAllProducts().then((value) => {
      setdata(value);
      d = value;
      console.log("d ", d);
    });
  }, []);

  const cardItem = (item) => {
    return (
      <Card className="my-5 py-4" style={{ width: "35rem" }}>
        <div style={{ width: "32rem", height: "24rem" }}>
          <Card.Img src={item.productImg} variant="top" />
        </div>
        <Card.Body className="text-center my-5">
          <Card.Title>{item.productName}</Card.Title>
          <Card.Text>{item.category}</Card.Text>
          <Card.Text>â‚¹{item.price}</Card.Text>
          {/* <Card.Text>{item.description}</Card.Text> */}

          <NavLink
            to={"/product/" + item.productId}
            className="btn btn-outline-primary ms-2"
          >
            Buy Now
          </NavLink>
        </Card.Body>
      </Card>
    );
  };

  return (
    <div>
      <Container className="py-5">
        <Row>
          <Col className="text-center">
            <h1>Product</h1>
            <hr />
          </Col>
        </Row>
      </Container>
      <Container>
        <Row className="justify-content-around">
          {/* using React.childer to Arrya to set key automatically for the items of list */}
          {React.Children.toArray(data.map(cardItem))}
        </Row>
      </Container>
    </div>
  );
}

export default Product;
