import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import axios from "axios";
/* import { DATA } from "../Data"; */

function Product() {
  const [DATA, setDATA] = useState();
  useEffect(() => {
    setDATA(getAllProducts());
  }, []);

  const cardItem = (item) => {
    return (
      <Card style={{ width: "18rem" }}>
        <Card.Img variant="top" src="holder.js/100px180" />
        <Card.Body>
          <Card.Title>Card Title</Card.Title>
          <Card.Text>${item}</Card.Text>
          <Button variant="primary">Go somewhere</Button>
        </Card.Body>
      </Card>
    );
  };

  return (
    <div>
      <div className="container py-5">
        <div className="row">
          <div className="col-12 text-center">
            <h1>Product</h1>
            <hr />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row">{DataTransfer.map(cardItem)}</div>
      </div>
    </div>
  );
}

async function getAllProducts() {
  let url = "localhost:8084/product-service/product/allproducts";
  let axiosResponse;
  let result;
  let error = "";
  try {
    axiosResponse = await axios.get(url).catch((err) => {
      if (err.response.status !== 200) {
        console.log(err.response.status);
        error = err.response.status;
        throw new Error("CANNOT FETCH PRODUCT");
      }
      throw err;
    });
  } catch (err) {
    alert(err);
    result = err;
  }
  if (error === "") {
    result = axiosResponse.data;
    console.log(result);
  }
  return result;
}

export default Product;
