import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { allItem,delItem ,addItem} from "../redux/actions/index";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CloseButton from "react-bootstrap/CloseButton";
import { NavLink } from "react-router-dom";
import AddToCartAxios from "./AddToCartAxios";
import UpdateItemQtyAxios from "./UpdateItemQtyAxios";
import GetAllItemsAxios from "./GetAllItemsAxios";


const Cart = () => {
  const state1 = useSelector((state1) => state1.allItem);
  const state = useSelector((state) => state.addItem);
  const dispatch = useDispatch();
  useEffect(()=>{
    
    GetAllItemsAxios.GetAllItemsAxios().then((value)=>{
      console.log("cart ", value);
      if(!(value.length==1&&value[0]==="AUTHORIZATION FAILED"))
        {
          if(state1.length<1)
          {
          dispatch(allItem(value));
          }
        }
    });
  
  },[state]);
  

  const handleClose = (item) => {
    DeleteItemsAxios.deleteItemsAxios(
      "http://localhost:8084/cart-service/cart/updateCart/deleteItem/" +
      item.productName ).then((value) => {
    dispatch(delItem(item));
    })
  };

  const changeQty = (name,event) => {
    event.preventDefault();
    let qan = document.getElementById("qtyChange").value;
    console.log("new quantity", qan)
    UpdateItemQtyAxios.updateItemQtyAxios("http://localhost:8084/cart-service/cart/updateCart/itemQuantity/"+name+"/"+qan)

  };

  const cartItems = (cartItem) => {
    //AddToCartAxios.addToCartAxios("http://localhost:8084/cart-service/cart/addcart");
    return (
      <div className="px-4 bg-light rounded-3" key={cartItem.productId}>
        <Container className="py-4">
          <CloseButton
            onClick={() => handleClose(cartItem)}
            className="float-end"
          />
          <Row className="justify-content-center">
            <Col>
              <img
                src={cartItem.productImg}
                alt={cartItem.productName}
                height="200px"
                width="180px"
              />
            </Col>
            <Col>
              <h3>{cartItem.productName}</h3>
              <p className="lead fw-bolder">â‚¹{cartItem.price}</p>
              <p className="lead fw-bolder">QUNATITY :{'  '}{cartItem.quantity}</p>
              <form onSubmit={changeQty} className="card p-2">
              <div className="input-group">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Quantity"
                  id={"qtyChange"+cartItem.productName}
                  defaultValue={cartItem.quantity}
                />
                <button type="submit" className="btn btn-secondary">
                  Change Quantity
                </button>
              </div>
            </form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  };

  const emptyCart = () => {
    return (
      <div className="px-4 my-5 bg-light rounded-3">
        <Container className="py-4">
          <Row>
            <h3>Your Cart is Empty</h3>
          </Row>
        </Container>
      </div>
    );
  };

  const button = () => {
    return (
      <Container>
        <Row>
          <NavLink
            to="/checkout"
            className="btn btn-outline-primary mb-5 w-25 mx-auto"
          >
            Proceed To Checkout
          </NavLink>
        </Row>
      </Container>
    );
  };

  return (
    <>
      {state1.length === 0 && emptyCart()}
      {state1.length !== 0 && state1[0].map(cartItems)}
      {state1.length !== 0 && button()}
    </>
  );
};

export default Cart;
