import React from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { delItem } from "../redux/actions/index";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CloseButton from "react-bootstrap/CloseButton";
import { NavLink } from "react-router-dom";

const Cart = () => {
  const state = useSelector((state) => state.addItem);
  const dispatch = useDispatch();

  const handleClose = (item) => {
    dispatch(delItem(item));
  };

  const cartItems = (cartItem) => {
    return (
      <div className="px-4 bg-light rounded-3" key={cartItem.productId}>
        <Container className="py-4">
          <CloseButton
            onClick={() => handleClose(cartItem)}
            className="float-end"
          />
          <Row className="justify-content-center">
            <Col>
              <img
                src={cartItem.productImg}
                alt={cartItem.productName}
                height="200px"
                width="180px"
              />
            </Col>
            <Col>
              <h3>{cartItem.productName}</h3>
              <p className="lead fw-bolder">â‚¹{cartItem.price}</p>
              <p className="lead fw-bolder">QUNATITY :{'  '}{cartItem.quantity}</p>
            </Col>
          </Row>
        </Container>
      </div>
    );
  };

  const emptyCart = () => {
    return (
      <div className="px-4 my-5 bg-light rounded-3">
        <Container className="py-4">
          <Row>
            <h3>Your Cart is Empty</h3>
          </Row>
        </Container>
      </div>
    );
  };

  const button = () => {
    return (
      <Container>
        <Row>
          <NavLink
            to="/checkout"
            className="btn btn-outline-primary mb-5 w-25 mx-auto"
          >
            Proceed To Checkout
          </NavLink>
        </Row>
      </Container>
    );
  };

  return (
    <>
      {state.length === 0 && emptyCart()}
      {state.length !== 0 && state.map(cartItems)}
      {state.length !== 0 && button()}
    </>
  );
};

export default Cart;
