const allItem = [];
const allItems = (state = allItem, action) => {
  switch (action.type) {
    case "ADDITEM":

     
      return [...state, action.payload];
      break;

    case "DELITEM":
      return (state = state.filter((x) => {
        return x.productId !== action.payload.productId;
      }));
      break;

    default:
      return state;
      break;
  }
};

export default allItems;
