import React, { useState, useEffect } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import axios from "axios";
/* import { DATA } from "../Data"; */


function Product() {
  const [data, setdata] = useState([])
  let d;
  useEffect(() => {
   
    getAllProducts().then((value) => {
      setdata(value);
      d=value;
      console.log("test svsvs ",d);
    });
    
  },[]);

  const cardItem = (item) => {
    return (
      <Card style={{ width: "18rem" }}>
        <Card.Img variant="top" src="holder.js/100px180" />
        <Card.Body>
          <Card.Title>{item.productName}</Card.Title>
          <Card.Text></Card.Text>
          <Button variant="primary">Go somewhere</Button>
        </Card.Body>
      </Card>
    );
  };

  return (
    <div>
      <div className="container py-5">
        <div className="row">
          <div className="col-12 text-center">
            <h1>Product</h1>
            <hr />
          </div>
        </div>
      </div>
      <div className="container">

        <div className="row">{data.map(cardItem)}</div>  
      </div>
    </div>
  );
}

async function getAllProducts() {
  let url = "http://localhost:8084/cart-service/cart/available-items";
  let axiosResponse;
  let result;
  let error = "";
  console.log("in func")
  try {
    axiosResponse = await axios.get(url).catch((err) => {
      
     
      if (err.response.status !== 200) {
        console.log(err.response.status);
        error = err.response.status;
        throw new Error("CANNOT FETCH PRODUCT");
      }
      throw err;
    });
  } catch (err) {
    alert(err);
    result = err;
  }
  if (error === "") {
    result = axiosResponse.data;
    
    
  }
  console.log("resp ",axiosResponse)
  
  return result
}

export default Product;
